import React from "react";

export const About = () => {
  return (
    <section className="px-6 py-16 bg-gray-900 text-white">
      <div className="max-w-3xl mx-auto text-center">
        <h2 className="text-3xl font-bold mb-6 border-b-2 border-gray-600 inline-block">
          About Me
        </h2>
        <p className="text-lg leading-relaxed text-gray-300 mt-4">
          I'm <strong>Gokularam M</strong>, a 2nd-year Computer Science and Engineering student at
          <strong> SRM Madurai College of Engineering and Technology</strong>, Tamil Nadu, with a
          current CGPA of 7.14. I’m passionate about building web applications and solving
          real-world problems using technology.
        </p>
        <p className="text-lg leading-relaxed text-gray-300 mt-4">
          My goal is to become a Scientist/Engineer at ISRO and contribute to impactful tech
          innovations. I’m currently exploring full-stack development with the MERN stack and
          looking for internship opportunities to gain real-world experience.
        </p>
      </div>
    </section>
  );
};
